        frappe.msgprint(__("End date cannot be before than Start Date"));
		frappe.validated = false;
    }
	},
	date_validation:function(frm){
	    if(frm.doc.end < frm.doc.start){
	        frappe.msgprint(__("Start date cannot be after than End Date"));
			frappe.validated = false;
	    }
	},
	rejoin_validation:function(frm){
	    if(frm.doc.re_join < frm.doc.end){
        frappe.msgprint(__("Rejoining date cannot be before than End Date"));
		frappe.validated = false;
    }
	},
    total_leave_in_days:function(frm){
        if(frm.doc.start && frm.doc.end){
            var end = frappe.datetime.obj_to_str(frm.doc.end);
            var start = frappe.datetime.obj_to_str(frm.doc.start);
            var datediff = frappe.datetime.get_day_diff(end,start);
            frm.set_value("total_leave_in_days",datediff+1);
        }
    },
    late_joining_in_days:function(frm){
    if(frm.doc.reporting_date && frm.doc.re_join){      
        var rejoin = frappe.datetime.obj_to_str(frm.doc.re_join);
        var reporting_date = frappe.datetime.obj_to_str(frm.doc.reporting_date);
        var difference = frappe.datetime.get_day_diff(reporting_date,rejoin);
        if(difference >=0){
            frm.set_value("late_joining_in_days",difference);
        }
        else{
            frm.set_value("late_joining_in_days",0);
        }
    }
    }
});

