	cals(frm){
	    console.log("Hi");
        var nodw = frm.doc.no_of_days_worked;
        var bas = frm.doc.basic;
        var ebasic = (bas/30)* ((nodw) - frm.doc.absent_days);
        var fd_al = frm.doc.food;
        var fd = (fd_al/30)*((nodw) - frm.doc.absent_days);
        var me_al = frm.doc.mobile_allowance;
