    // },
    total_worked(frm){
         if (frm.doc.total_worked) {
            if(frm.doc.grade == 'Office Staff' || frm.doc.grade == 'Factory Staff'){
                var eligible_gratuity = (30 / 365) * frm.doc.total_worked
                frm.set_value('eligible_gratuity', eligible_gratuity.toFixed(2))
            }
            else{
                 var eligible_gratuity = (21 / 365) * frm.doc.total_worked
                frm.set_value('eligible_gratuity', eligible_gratuity.toFixed(2))
            }
         }
    },
    validate(frm) {
        frm.trigger("leaves_encashed")
        var gratuity_amount = (frm.doc.basic_salary / 30) * frm.doc.eligible_gratuity
        frm.set_value('gratuity_amount', Math.round(gratuity_amount))
        if (frm.doc.employee) {
            var basic = frm.doc.basic_salary
            var dow = frm.doc.no_of_days_worked
            var cal = (((basic / dow) / 8) * 1.25) * frm.doc.normal_over_time
            frm.set_value('amount', Math.floor(cal.toFixed(2)))
        }
        if (frm.doc.employee) {
            var ot = frm.doc.amount
            var wot = frm.doc.wot_amount
            var hot = frm.doc.hot_amount
            var cal = ot + wot + hot
            if (cal) {
                frm.set_value('total_ot', cal.toFixed(2))
            }
        }
        if (frm.doc.employee) {
            var leave_pay = (frm.doc.leave_pay)
            var gra_amount = Math.ceil(frm.doc.gratuity_amount)
            var leave_grat = (parseInt(leave_pay) + parseInt(gra_amount)) + (parseInt(frm.doc.additions))- (frm.doc.air_ticket_deduction + frm.doc.loan_other_deduction)
            if (leave_grat) {
                frm.set_value('leave_gratuity_total', leave_grat.toFixed(2))
                var net_pay = leave_grat + frm.doc.total_salary
                frm.set_value('net_pay', net_pay)
            }
        }
        if (frm.doc.employee) {
            var basic = frm.doc.basic_salary
            var dow = frm.doc.no_of_days_worked
            var cal = (((basic / dow) / 8) * 2.0) * frm.doc.holiday_over_time
            if (cal) {
                frm.set_value('hot_amount', cal.toFixed(2))
            }
        }
        if (frm.doc.employee) {
            var basic = frm.doc.basic_salary
            var dow = frm.doc.no_of_days_worked
            var cal = (((basic / dow) / 8) * 1.5) * frm.doc.friday_over_time
            if (cal) {
                frm.set_value('wot_amount', cal.toFixed(2))
            }
        }
        if (frm.doc.employee) {
            var basic = frm.doc.basic_salary
            var dow = frm.doc.total_working_days
            var earned_basic = ((basic / dow) * (frm.doc.no_of_days_worked- frm.doc.absent_days))
            if (earned_basic) {
                frm.set_value('earned_basic', earned_basic.toFixed(2))
            }
            
            var earned_food_allowance = ((frm.doc.food_allowance/dow)*(frm.doc.no_of_days_worked - frm.doc.absent_days))
            if (earned_food_allowance) {
                frm.set_value('earned_food_allowance', earned_food_allowance.toFixed(2))
            }
            var tott = (parseFloat(frm.doc.total_ot) + parseFloat(frm.doc.earned_basic)+ parseFloat(frm.doc.hra_pay )+ parseFloat(frm.doc.month_allowance)+parseFloat(frm.doc.earned_food_allowance)+parseFloat(frm.doc.prodallowance)+parseFloat(frm.doc.arrears))-(parseFloat(frm.doc.monthly_deduction))
            frm.set_value('total_salary',tott)
        }
        if(frm.doc.employee){
            var date1 = new Date(frm.doc.date_of_joining);
            var date2 = new Date(frm.doc.pay_end_date);
            var diffTime = Math.abs(date2 - date1);
            var diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24)); 
            frm.set_value('employment_duration',diffDays +1)
            frm.set_value('total_worked',diffDays +1)
    
         }   
        if (frm.doc.employee) {
            var a = frm.doc.basic_salary / 30
            var b = frm.doc.basic_salary / 31
            var x = frm.doc.no_of_days_worked - 31
            var y = (x) * b
            var p = 31
            var q = p * a
            var z = q + y - 2
        }
       
    },
    onload(frm) {
        frm.call('get_reg_form').then(r=>{
			if (r.message) {
			    console.log(r)
				frm.set_value('resignation_form', r.message[0])
                frm.set_value('pay_start_date', r.message[1])
                frm.set_value('pay_end_date', r.message[2])
			}
		})
        
        frm.call('calculate_attendance').then(r=>{
			if (r.message) {
				var start = new Date(frm.doc.pay_start_date);
                    var end = new Date(frm.doc.pay_end_date);
                    var diffDays = end.getDate() - start.getDate()
                    frm.set_value('no_of_days_worked', diffDays + 1)
                    frm.set_value('normal_over_time', r.message[0].ot)
                    frm.set_value('friday_over_time', r.message[0].wot)
                    frm.set_value('holiday_over_time', r.message[0].hot)
			}
		})
        frm.call('get_current_month_date').then(r=>{
			if (r.message) {
				frm.set_value('total_working_days', r.message)
			}
		})
            
    },
    
    leaves_encashed(frm) {
        if(frm.doc.leaves_encashed){
            var basic = frm.doc.basic_salary
            var leave = frm.doc.leaves_encashed
            var tw = 30
            console.log(basic)
            console.log(leave)
            console.log(tw)
            var cal = ((basic / tw) * leave)
            frm.set_value('leave_pay', cal.toFixed(2))
        }
        
    }
})
